def merge_dicts(**kwargs):
    """Merge multiple dictionaries into one."""

    return kwargs


# Test cases
print(merge_dicts(a=1, b=2))     # Should print: {'a': 1, 'b': 2}
print(merge_dicts(b=2, c=3))     # Should print: {'c': 3, 'b': 2}
print(merge_dicts())             # Should print: {}
print(merge_dicts(a=1))          # Should print: {'a': 1}
```

